  }
  
  return (
    <>
      <Button><i class="bi bi-plus-lg" variant="success" onClick={handleShow}> Agregar Marcador</i></Button>

      <Modal show={show} onHide={handleClose, handleSubmit}>
        <Modal.Header closeButton>
          <Modal.Title>Agrega tu marcador</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3" controlId="formBasicName">
              <Form.Label>Nombre</Form.Label>
              <Form.Control type="text" placeholder="Nombre" />
              <Form.Text className="text-muted">
              </Form.Text>
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicUrl">
              <Form.Label>URL</Form.Label>
              <Form.Control type="text" placeholder="URL" />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicDescrip">
              <Form.Label>Descripcion</Form.Label>
              <Form.Control type="text" placeholder="Descripcion" />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Cerrar
          </Button>
          <Button variant="primary" onClick={handleSubmit}>
            Guardar cambios
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default ModalComponent;
