import React, { useState } from 'react';
import { getAll, saveMarcador } from "../services/TrabajoServices";
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';

const initialMarcador = [
  {
      id: 1,
      nombreMarcador: "",
      urlMarcador: "",
      descripcionMarcador: "",
      prioridad: 0,
      categoria: {},
      usuarios: {}
  }
]


function ModalComponent({}) {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const [marcadores, setMarcadores] = useState(initialMarcador);

  const {nombreMarcador, urlMarcador, descripcionMarcador} = marcadores;

//   const marcadorAdd = async (marcador) => {
//     await saveMarcador(marcador)
//     setMarcadores(await getAll())
// }

  const handleSubmit = (e) => {
    e.preventDefault();//Esto es para evitar que la pagina se actualize al presionar el button
    marcadorAdd(marcadores);
  }

  return (
    <>
      <Button><i class="bi bi-plus-lg" variant="success" onClick={handleShow}> Agregar Marcador</i></Button>

      <Modal show={show} onHide={handleClose} >
        <Modal.Header closeButton>
          <Modal.Title>Agrega tu marcador</Modal.Title>
        </Modal.Header>
        <Modal.Body>
        <form onSubmit={handleSubmit}>
        <div className='form-group'>
            <label>Nombre</label>
            <input type='text' className='form-control' id='nombreMarcador' placeholder='Marca' value={nombreMarcador} name='marca' />
            <br />
        </div>
        <div className='form-group'>
            <label>Url</label>
            <input type='text' className='form-control' id='urlMarcador' placeholder='Modelo' value={urlMarcador} name='modelo' />
            <br />
        </div>
        <div className='form-group'>
            <label>Descripcion</label>
            <input type='text' className='form-control' id='descripcionMarcador' placeholder='Color' value={descripcionMarcador} name='color' />
            <br />
        </div>
        <br/>
        <button type='submit' className='btn btn-outline-primary me-2'>Agregar</button>

    </form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Cerrar
          </Button>
          
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default ModalComponent;
